resources.string:
CREATE TABLE IF NOT EXISTS `user_decision` (
    `user_decision_id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT
    );

CREATE TABLE IF NOT EXISTS `inferred_decision` (
   `inferred_decision_id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT
   );

CREATE TABLE IF NOT EXISTS `data` (
    `data_id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT
    );

CREATE TABLE IF NOT EXISTS `consumer` (
   `consumer_id`	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT
   );
